import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Alert,
} from 'react-native';
import { Calendar } from 'react-native-calendars';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import * as Haptics from 'expo-haptics';

import { theme } from '../styles/theme';
import { commonStyles } from '../styles/common';
import { getPeriodData, addPeriodData } from '../services/database';

export default function PeriodPlannerScreen() {
  const [periodData, setPeriodData] = useState({});
  const [markedDates, setMarkedDates] = useState({});
  const [selectedDate, setSelectedDate] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadPeriodData();
  }, []);

  const loadPeriodData = async () => {
    try {
      const data = await getPeriodData();
      const processedData = {};
      const marked = {};

      data.forEach(item => {
        processedData[item.date] = item;
        marked[item.date] = getMarkerForType(item.type);
      });

      setPeriodData(processedData);
      setMarkedDates(marked);
    } catch (error) {
      console.error('Error loading period data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getMarkerForType = (type) => {
    switch (type) {
      case 'period':
        return {
          selected: true,
          selectedColor: theme.colors.error,
          selectedTextColor: theme.colors.surface,
        };
      case 'ovulation':
        return {
          marked: true,
          dotColor: theme.colors.success,
          selectedColor: theme.colors.success,
          selectedTextColor: theme.colors.surface,
        };
      case 'fertile':
        return {
          selected: true,
          selectedColor: theme.colors.warning,
          selectedTextColor: theme.colors.text,
        };
      default:
        return {};
    }
  };

  const handleDayPress = (day) => {
    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
    setSelectedDate(day.dateString);
    
    const existingData = periodData[day.dateString];
    if (existingData) {
      showDayInfo(day.dateString, existingData);
    } else {
      showAddDataOptions(day.dateString);
    }
  };

  const showDayInfo = (date, data) => {
    const formattedDate = new Date(date).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });

    Alert.alert(
      `${formattedDate}`,
      `Type: ${data.type.charAt(0).toUpperCase() + data.type.slice(1)}\n${data.notes || ''}`,
      [
        { text: 'Remove', onPress: () => removePeriodData(date), style: 'destructive' },
        { text: 'OK', style: 'default' },
      ]
    );
  };

  const showAddDataOptions = (date) => {
    const formattedDate = new Date(date).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });

    Alert.alert(
      `Mark ${formattedDate}`,
      'What would you like to mark this day as?',
      [
        { text: 'Cancel', style: 'cancel' },
        { text: 'ðŸ”´ Period Day', onPress: () => addData(date, 'period', 'Period day') },
        { text: 'ðŸŒ¿ Ovulation', onPress: () => addData(date, 'ovulation', 'Ovulation day') },
        { text: 'ðŸ’› Fertile Window', onPress: () => addData(date, 'fertile', 'Fertile window') },
      ]
    );
  };

  const addData = async (date, type, notes) => {
    try {
      await addPeriodData(date, type, notes);
      await loadPeriodData();
      Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
    } catch (error) {
      console.error('Error adding period data:', error);
      Alert.alert('Error', 'Failed to add data. Please try again.');
    }
  };

  const removePeriodData = async (date) => {
    try {
      // Note: You'd need to implement a delete function in database.js
      // For now, we'll just reload the data
      await loadPeriodData();
      Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
    } catch (error) {
      console.error('Error removing period data:', error);
    }
  };

  const predictNextPeriod = () => {
    const periodDates = Object.keys(periodData)
      .filter(date => periodData[date].type === 'period')
      .sort()
      .reverse();

    if (periodDates.length < 2) return null;

    // Simple prediction based on average cycle length
    const cycles = [];
    for (let i = 0; i < periodDates.length - 1; i++) {
      const current = new Date(periodDates[i]);
      const previous = new Date(periodDates[i + 1]);
      const diff = Math.abs(current - previous) / (1000 * 60 * 60 * 24);
      cycles.push(diff);
    }

    const averageCycle = cycles.reduce((a, b) => a + b, 0) / cycles.length;
    const lastPeriod = new Date(periodDates[0]);
    const nextPeriod = new Date(lastPeriod.getTime() + (averageCycle * 24 * 60 * 60 * 1000));

    return nextPeriod;
  };

  const LegendItem = ({ color, icon, label, description }) => (
    <View style={[commonStyles.row, { marginBottom: theme.spacing.sm }]}>
      <View style={{
        width: 20,
        height: 20,
        borderRadius: 10,
        backgroundColor: color,
        marginRight: theme.spacing.sm,
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        {icon && <Text style={{ fontSize: 12 }}>{icon}</Text>}
      </View>
      <View style={{ flex: 1 }}>
        <Text style={[commonStyles.bodyText, { fontFamily: theme.fonts.semiBold }]}>
          {label}
        </Text>
        <Text style={[commonStyles.bodyText, { fontSize: theme.fontSizes.sm, color: theme.colors.textSecondary }]}>
          {description}
        </Text>
      </View>
    </View>
  );

  const nextPeriod = predictNextPeriod();

  if (loading) {
    return (
      <View style={commonStyles.centerContainer}>
        <Ionicons name="calendar-outline" size={48} color={theme.colors.primary} />
        <Text style={[commonStyles.subtitle, { marginTop: theme.spacing.md }]}>
          Loading calendar...
        </Text>
      </View>
    );
  }

  return (
    <LinearGradient
      colors={[theme.colors.background, theme.colors.surface]}
      style={commonStyles.container}
    >
      <ScrollView
        style={{ flex: 1 }}
        contentContainerStyle={{ paddingBottom: theme.spacing.xxl }}
        showsVerticalScrollIndicator={false}
      >
        {/* Header */}
        <View style={{ alignItems: 'center', padding: theme.spacing.md, marginBottom: theme.spacing.lg }}>
          <View style={[commonStyles.heartContainer, { marginBottom: theme.spacing.md }]}>
            <Ionicons name="calendar" size={32} color={theme.colors.primary} />
          </View>
          <Text style={[commonStyles.title, { marginBottom: theme.spacing.sm }]}>
            Period Planner ðŸŒ¸
          </Text>
          <Text style={[commonStyles.scriptText, { textAlign: 'center' }]}>
            "Tracking your cycle with love and care"
          </Text>
        </View>

        {/* Next Period Prediction */}
        {nextPeriod && (
          <View style={[
            commonStyles.card,
            {
              backgroundColor: theme.colors.romantic,
              alignItems: 'center',
              marginHorizontal: theme.spacing.md,
              marginBottom: theme.spacing.lg,
            }
          ]}>
            <Ionicons name="time-outline" size={24} color={theme.colors.primary} />
            <Text style={[
              commonStyles.bodyText,
              {
                fontFamily: theme.fonts.semiBold,
                marginTop: theme.spacing.sm,
                marginBottom: theme.spacing.xs,
              }
            ]}>
              Next Period Prediction
            </Text>
            <Text style={[commonStyles.bodyText, { color: theme.colors.textSecondary }]}>
              {nextPeriod.toLocaleDateString('en-US', {
                weekday: 'long',
                month: 'long',
                day: 'numeric',
              })}
            </Text>
          </View>
        )}

        {/* Calendar */}
        <View style={[commonStyles.card, { marginHorizontal: theme.spacing.md }]}>
          <Calendar
            markedDates={markedDates}
            onDayPress={handleDayPress}
            theme={{
              backgroundColor: 'transparent',
              calendarBackground: 'transparent',
              textSectionTitleColor: theme.colors.text,
              selectedDayBackgroundColor: theme.colors.primary,
              selectedDayTextColor: theme.colors.surface,
              todayTextColor: theme.colors.primary,
              dayTextColor: theme.colors.text,
              textDisabledColor: theme.colors.textLight,
              dotColor: theme.colors.primary,
              selectedDotColor: theme.colors.surface,
              arrowColor: theme.colors.primary,
              monthTextColor: theme.colors.text,
              indicatorColor: theme.colors.primary,
              textDayFontFamily: theme.fonts.regular,
              textMonthFontFamily: theme.fonts.semiBold,
              textDayHeaderFontFamily: theme.fonts.medium,
              textDayFontSize: 16,
              textMonthFontSize: 18,
              textDayHeaderFontSize: 14,
            }}
          />
        </View>

        {/* Legend */}
        <View style={[commonStyles.card, { marginHorizontal: theme.spacing.md, marginTop: theme.spacing.lg }]}>
          <Text style={[
            commonStyles.subtitle,
            {
              textAlign: 'left',
              marginBottom: theme.spacing.md,
            }
          ]}>
            Legend
          </Text>
          
          <LegendItem
            color={theme.colors.error}
            label="Period Days"
            description="Menstruation days"
          />
          
          <LegendItem
            color={theme.colors.success}
            icon="ðŸŒ¿"
            label="Ovulation"
            description="Most fertile day"
          />
          
          <LegendItem
            color={theme.colors.warning}
            label="Fertile Window"
            description="High fertility days"
          />
        </View>

        {/* Instructions */}
        <View style={[
          commonStyles.card,
          {
            backgroundColor: theme.colors.secondary,
            marginHorizontal: theme.spacing.md,
            marginTop: theme.spacing.lg,
          }
        ]}>
